import React, { useState, useEffect  } from 'react';
import { Link } from 'react-router-dom';
import ActivityChoiceCard from './ActivityChoiceCard.js'
import { FaClock, FaGrinBeamSweat, FaSun } from 'react-icons/fa';
import bgYoga from '../assets/yoga-6.jpg'
import './pages.css'

const YogaChoice = () => {

  useEffect(() => {
  window.scrollTo(0, 0)
  }, [])


  let clockStyle = { color: "blue", fontSize: "2.5em", marginLeft: "10px" };
  let sweatStyle = { color: "red", fontSize: "2.5em", marginLeft: "10px" };
  let sunStyle = { color: "green", fontSize: "2.5em", marginLeft: "10px" };

  const [durationValue, setDurationValue] = useState(0);
  const [intensityValue, setIntensityValue] = useState(0);
  const [timeValue, setTimeValue] = useState("");
  const [counter, setCounter] = useState(0);



  const handleDropDownChange1 = (e) => {
    setDurationValue(e.target.value);
    setCounter(counter+1);

  }

  const handleDropDownChange2 = (e) => {
    setIntensityValue(e.target.value);
    setCounter(counter+1);

  }

  const handleDropDownChange3 = (e) => {
    setTimeValue(e.target.value);
    setCounter(counter+1);

  }

  const handleSubmit = () => {

    //   if (durationValue == 15 && intensityValue == 1 && timeValue === "Morning") {

    //     dispatch(changeURL('https://www.youtube.com/watch?v=uBrSRnK_hIg&t=8s'))
    //     //setURL("https://www.youtube.com/watch?v=uBrSRnK_hIg&t=8s")

    //   } else if (durationValue == 15 && intensityValue == 1 && timeValue === "Evening") {

    //     dispatch(changeURL('https://youtu.be/b1RTVla9wsY'))

    //   } else if (durationValue == 15 && intensityValue == 2 && timeValue === "Morning") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 15 && intensityValue == 2 && timeValue === "Evening") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 30 && intensityValue == 1 && timeValue === "Morning") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 30 && intensityValue == 1 && timeValue === "Evening") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 30 && intensityValue == 2 && timeValue === "Morning") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 30 && intensityValue == 2 && timeValue === "Evening") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 45 && intensityValue == 1 && timeValue === "Morning") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 45 && intensityValue == 1 && timeValue === "Evening") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 45 && intensityValue == 2 && timeValue === "Morning") {

    //     dispatch(changeURL())

    //   } else if (durationValue == 45 && intensityValue == 2 && timeValue === "Evening") {

    //     dispatch(changeURL())

    //   }


  }

  return (
    <div>
   <div className="card activity-header"  >
      <div>
        <img src={bgYoga} alt="A guy rolling out a yoga mat" id="activity-inter" />
        </div>
        <div className="card-img-overlay text-light" id="activity-choice-text">
          <h1 id="activity-heading">A few questions...</h1>
          <br/>
        <p className="text-light"><span>We have just <span className="text-secondary">three questions</span> to ask you. </span><br></br><span>These questions will help us ensure you have an </span><br></br><span> <span className="text-secondary">experience tailored to your availability and skill level.</span></span><br></br> <span>Check them out below!</span><br></br><span></span></p>
        </div>
       </div> 
    <div className="choice">
        <div className="card-group row no-gutters">
          <div className="col duration" >
            <div className="icon clock-icon">
              <FaClock style={clockStyle} />
            </div>
            <ActivityChoiceCard
              function={handleDropDownChange1}
              question={"How long do you have to spare today?"}
              value1={15}
              value2={30}
              value3={45}
              answer1={"15 minutes"}
              answer2={"30 minutes"}
              answer3={"45 minutes"}/>
            </div>
          <div className="col intensity" >
            <div className="icon clock-icon">
              <FaGrinBeamSweat style={sweatStyle} />
            </div>
            <ActivityChoiceCard
              function={handleDropDownChange2}
              question={"What intensity level would you like?"}
              value1={1}
              value2={2}
              answer1={"1"}
              answer2={"2"}
                  />
            </div>
          <div className="col time" >
            <div className="icon clock-icon">
              <FaSun style={sunStyle} />
            </div>
            <ActivityChoiceCard
              function={handleDropDownChange3}
              value1={"Morning"}
              value2={"Evening"}
              question={"What time of the day is it currently?"}
              answer1={"Morning"}
              answer2={"Evening"}
                />
          </div>

        </div>
        {counter === 3 &&
          <Link to={"/YogaContent"} className="btn btn-primary" onClick={() => handleSubmit()}>Submit</Link>
        }
      </div>
    </div>  
    );
  }


export default YogaChoice;
