import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import ActivityChoiceCard from './ActivityChoiceCard.js';
import './pages.css';

class MeditationChoice extends Component {

  constructor(props) {
    super(props);

    this.state = {

      selectDurationValue: "",
      selectIntensityValue: "",
      audioChoice: ""

    };


  }
  handleDropDownChange1 = (e) => {

    this.setState({ selectDurationValue: e.target.value });

  }

  handleDropDownChange2 = (e) => {

    this.setState({ selectIntensityValue: e.target.value });
  }

  handleDropDownChange3 = (e) => {

    this.setState({ selectTimeValue: e.target.value });
  }

  handleSubmit = () => {
    console.log("Duration :" + this.state.selectDurationValue);
    console.log("Intensity :" + this.state.selectIntensityValue);
    console.log("Time : " + this.state.selectTimeValue);


  }
  render() {

    return (
      <div className="">
        <div className="card-group">
          <div className="col-3">
            <ActivityChoiceCard
              function={ this.handleDropDownChange1 }
              question={"How long do you have to spare today?"}
              value1={5}
              value2={10}
              value3={20}
              answer1={"5 minutes"}
              answer2={"10 minutes"}
              answer3={"20 minutes"} />
          </div>
          <div className="col-3">
            <ActivityChoiceCard
              function={ this.handleDropDownChange2 }
              question={"What intensity level would you like?"}
              value1={1}
              value2={2}
              value3={3}
              answer1={"1"}
              answer2={"2"}
              answer3={"3"} />
          </div>
          <div className="col-3">
            <ActivityChoiceCard
              function={ this.handleDropDownChange3 }
              question={"What time of the day is it currently?"}
              value1={"Morning"}
              value2={"Evening"}
              answer1={"Morning"}
              answer2={"Evening"}
            />
          </div>
        </div>

        <Link to={"/MeditationContent"} className="btn btn-primary" onClick={() => this.handleSubmit() }>Submit</Link>
      </div>


    )
  }
};

export default MeditationChoice;
