import React from 'react';
// import YouTube from 'react-youtube';
import ReactPlayer from 'react-player'
import VideoBar from './videoBar.js'
import { useSelector } from 'react-redux';

const YoutubePlayer = () => {

  const videoOnReady = (event) => {
    // access to player in all event handlers via event.target
    event.target.pauseVideo();
  }

  const opts = {
      height: '400',
      width: '680',
      playerVars: {
        // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
      origin: 'http://localhost:3000' 
      },
    };

  //let videoID  = useSelector(state => state.urlReducer);
  //const videoID = "uBrSRnK_hIg"
  let videoID = "https://www.youtube.com/watch?v=uBrSRnK_hIg&t=8s"
  return (

    // <YouTube videoid={videoID} opts={opts} onReady={videoOnReady} />
    <div className="container" id="vidWithBtns">

      <ReactPlayer url={videoID} controls={true} style={{ margin: 'auto', paddingTop: 20, paddingBottom: 10, }} height= "400" width= "680"/>
      <VideoBar />

    </div>

  )


}

export default YoutubePlayer
