import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { useState } from 'react';
import ActivityChoiceCard from './ActivityChoiceCard.js';
import './pages.css';

const MeditationChoice = () => {

    state = {

      selectDurationValue: "",
      selectIntensityValue: "",
      audioChoice: ""

    };

  const handleDropDownChange1 = (e) => {

    this.setState({ selectDurationValue: e.target.value });

  }

  const handleDropDownChange2 = (e) => {

    this.setState({ selectIntensityValue: e.target.value });
  }

  const handleDropDownChange3 = (e) => {

    this.setState({ selectTimeValue: e.target.value });
  }

  const handleSubmit = () => {
    console.log("Duration :" + this.state.selectDurationValue);
    console.log("Intensity :" + this.state.selectIntensityValue);
    console.log("Time : " + this.state.selectTimeValue);


  }
  render(


      <div className="">
        <div className="card-group">
          <div className="col-3">
            <ActivityChoiceCard
              function={ handleDropDownChange1 }
              question={"How long do you have to spare today?"}
              value1={5}
              value2={10}
              value3={20}
              answer1={"5 minutes"}
              answer2={"10 minutes"}
              answer3={"20 minutes"} />
          </div>
          <div className="col-3">
            <ActivityChoiceCard
              function={ handleDropDownChange2 }
              question={"What intensity level would you like?"}
              value1={1}
              value2={2}
              value3={3}
              answer1={"1"}
              answer2={"2"}
              answer3={"3"} />
          </div>
          <div className="col-3">
            <ActivityChoiceCard
              function={ handleDropDownChange3 }
              question={"What time of the day is it currently?"}
              value1={"Morning"}
              value2={"Evening"}
              answer1={"Morning"}
              answer2={"Evening"}
            />
          </div>
        </div>

        <Link to={"/MeditationContent"} className="btn btn-primary" onClick={() => this.handleSubmit() }>Submit</Link>
      </div>


    )
};

export default MeditationChoice;
